void ImageViewer::Disappear()
{
    //thumbnailCanvas->ContinueLoadingThumbnails();
    glPanel->Clear();
    ClearKeys();

    thumbnailCanvas->SetCursor(currentImageFileName);
    currentImageFileName = "";
    Show(false);
    thumbnailCanvas->SetFocus();
    disappearState = DISAPPEAR_STATE_NONE;
    thumbnailCanvas->SetFocus();
    imageBrowser->Show();
}

wxLongLong keyTime = 0;

void ImageViewer::OnKeyDown(wxKeyEvent &event)
{
    //int sortedImageNumber;
    wxLongLong kt = wxGetLocalTimeMillis();
    keyTime = kt;
    wxFileName nextImage;

	lastKeyCode = event.GetKeyCode();
    switch (event.GetKeyCode())
    {
    case WXK_DELETE:        
        nextImage = thumbnailCanvas->Jump(currentImageFileName, 1, viewableExtensions);

        if (nextImage == "")
            nextImage = thumbnailCanvas->Jump(currentImageFileName, -1, viewableExtensions);

        thumbnailCanvas->DeleteImage(currentImageFileName);

        if (nextImage == "")
        {
            disappearState = DISAPPEAR_STATE_REQUESTED;
        }
        else
        {
            DisplayImage(nextImage);
        }
        break;

    case WXK_ESCAPE:
	case WXK_RETURN:
        disappearState = DISAPPEAR_STATE_REQUESTED;
        break;

    default:
            if (event.GetKeyCode() < keys.size())
                keys[event.GetKeyCode()] = 1;
        break;
    }
}


void ImageViewer::OnKeyUp(wxKeyEvent &event)
{
    int  pageJump = 5;
    int  lineJump = 1;
    int imageJump = 1;

    if (thumbnailCanvas)
    {
        lineJump = thumbnailCanvas->GetNumColumns();
        pageJump = thumbnailCanvas->GetNumImagesPerPage();
    }
    if (event.ShiftDown())
    {
        imageJump = lineJump;
    }

    switch (event.GetKeyCode())
    {
    case WXK_ESCAPE:
        ClearKeys();
        break;

    case WXK_HOME:
        HomeImage();
        break;

    case WXK_END:
        EndImage();
        break;

    case 'R':
    case WXK_PAGEUP:
        PrevImage(imageJump);
        break;

    case 'F':
    case WXK_PAGEDOWN:
        NextImage(imageJump);
        break;

    case 'V':
        NextImage(pageJump);
        break;

    case '4':
        PrevImage(pageJump);
        break;

    default:
        if (event.GetKeyCode() < keys.size())
            keys[event.GetKeyCode()] = 0;
        break;
    }
}

void ImageViewer::OnClose(wxCloseEvent &event)
{
    Show(false);
    GetParent()->Show(false);
    wxGetApp().ExitMainLoop();
}

void ImageViewer::OnMouseWheel(wxMouseEvent &event)
{
    if (event.GetWheelRotation() > 0)
    {
        PrevImage();
    }

    if (event.GetWheelRotation() < 0)
    {
        NextImage();
    }

    event.Skip();
}


void ImageViewer::OnMouse(wxMouseEvent& event)
{
    if (event.LeftDClick())
    {
        disappearState = DISAPPEAR_STATE_REQUESTED;
    }
}

void ImageViewer::OnMouseLDClick(wxMouseEvent& event)
{
    Disappear();
}

